import sys
from PyQt5 import QtCore, QtGui, QtWidgets
from PyQt5.QtWidgets import QFileDialog, QGraphicsScene, QGraphicsPixmapItem
import cv2
from PyQt5.QtGui import QImage, QPixmap
from rs_big_pred import rs_big_pred
from PyQt5.QtCore import pyqtSignal, QObject
from PyQt5.QtCore import QThread

class RecognitionThread(QThread):
    finished = pyqtSignal(QImage)
    started = pyqtSignal()

    def __init__(self, file_path, model_path):
        super().__init__()
        self.file_path = file_path
        self.model_path = model_path
        self.pred_mask = None

    def run(self):
        # 这里执行耗时操作
        self.started.emit()
        pred_mask = rs_big_pred(self.file_path, self.model_path)
        pred_mask = cv2.cvtColor(pred_mask, cv2.COLOR_BGR2RGB)
        self.pred_mask = pred_mask
        height, width, channel = pred_mask.shape
        bytesPerLine = 3 * width
        qImg = QImage(pred_mask.data, width, height, bytesPerLine, QImage.Format_RGB888)
        self.finished.emit(qImg)

