    def browseImage(self):
        filePath, _ = QFileDialog.getOpenFileName()
        if filePath:
            self.lineEdit.setText(filePath)
            self.displayImage(filePath, self.originImage)

    def browseModel(self):
        filePath, _ = QFileDialog.getOpenFileName()
        if filePath:
            self.lineEdit_2.setText(filePath)

    def startRecognition(self):
        file_path = self.lineEdit.text()
        model_path = self.lineEdit_2.text()
        # 创建并启动线程
        self.thread = RecognitionThread(file_path, model_path)
        self.thread.started.connect(lambda: self.progressLabel.setText("识别中...请等待"))
        self.thread.finished.connect(self.onRecognitionFinished)
        self.thread.start()

    def onRecognitionFinished(self,qImg):
        self.progressLabel.setText("识别完成！")
        self.result_pred = self.thread.pred_mask
        pixmap = QPixmap.fromImage(qImg)
        scene = QGraphicsScene()
        scene.addItem(QGraphicsPixmapItem(pixmap))
        self.resultImage.setScene(scene)
        self.resultImage.fitInView(scene.itemsBoundingRect(), QtCore.Qt.KeepAspectRatio)

    def clearAll(self):
        self.lineEdit.clear()
        self.lineEdit_2.clear()
        self.lineEdit_3.clear()
        self.originImage.scene().clear()
        self.resultImage.scene().clear()

    def browseOutput(self):
        filePath, _ = QFileDialog.getSaveFileName()
        if filePath:
            self.lineEdit_3.setText(filePath)

    def exportOutput(self):
        path = self.lineEdit_3.text()
        cv2.imwrite(path,self.result_pred)

    def displayImage(self, filePath, view):
        image = cv2.imread(filePath)
        image = cv2.cvtColor(image, cv2.COLOR_BGR2RGB)
        height, width, channel = image.shape
        bytesPerLine = 3 * width
        qImg = QImage(image.data, width, height, bytesPerLine, QImage.Format_RGB888)
        pixmap = QPixmap.fromImage(qImg)
        scene = QGraphicsScene()
        scene.addItem(QGraphicsPixmapItem(pixmap))
        view.setScene(scene)
        view.fitInView(scene.itemsBoundingRect(), QtCore.Qt.KeepAspectRatio)

